"""Example of a Hello World TFX custom component.

This custom component simply reads tf.Examples from input and passes through as
output.  This is meant to serve as a kind of starting point example for creating
custom components.

This component along with other custom component related code will only serve as
an example and will not be supported by TFX team.
"""
from __future__ import absolute_import
from __future__ import division
from __future__ import print_function

from typing import Optional, Text, List
from main.components.embedding_eval_executor import Executor
from tfx import types
from tfx.dsl.components.base import base_component
from tfx.dsl.components.base import executor_spec
from tfx.types import channel_utils
from tfx.types import standard_artifacts
from tfx.types.component_spec import ChannelParameter
from tfx.types.component_spec import ExecutionParameter
from tfx.orchestration.data_types import RuntimeParameter
#from tfx.types.standard_component_specs import *

class EmbeddingEvaluatorSpec(types.ComponentSpec):
    """ComponentSpec for Custom TFX Evaluator for Metadata Component."""

    PARAMETERS = {
        # These are parameters that will be passed in the call to
        # create an instance of this component.
        'name': ExecutionParameter(type=Text, optional=True),
        'output_table': ExecutionParameter(type=Text)
    }
    INPUTS = {
        # This will be a dictionary with input artifacts, including URIs 
        'model': ChannelParameter(type=standard_artifacts.Model),
        #'embeddings': ChannelParameter(type=standard_artifacts.Examples),
        #'user_data': ChannelParameter(type=standard_artifacts.Examples),
    }
    
    OUTPUTS = {
        # This will be a dictionary which this component will populate
        #'output_data': ChannelParameter(type=standard_artifacts.Examples),
    }


class EmbeddingEvaluator(base_component.BaseComponent):
    """Custom TFX Evaluator for Metadata Component.

    This custom component class consists of only a constructor.
    """

    SPEC_CLASS = EmbeddingEvaluatorSpec
    EXECUTOR_SPEC = executor_spec.ExecutorClassSpec(Executor)

    def __init__(self,
                 model: types.Channel = None,
                 name: Optional[Text] = None,
                 output_table: Text = None):
        
        """
        Args:
          output_table: A path to locate input test id
          name: Optional unique name. Necessary if multiple evaluator components are
            declared in the same pipeline.
        """
        # output_data will contain a list of Channels for each split of the data,
        # by default a 'train' split and an 'eval' split. Since HelloComponent
        # passes the input data through to output, the splits in output_data will
        # be the same as the splits in input_data, which were generated by the
        # upstream component.

        spec = EmbeddingEvaluatorSpec(model=model,
                                      name=name,
                                      output_table=output_table)
        super(EmbeddingEvaluator, self).__init__(spec=spec)